mapper.select:
SELECT summoner_id, trunc(honor_rate/user_info.honor_cnt,1) as honor_rate, profile_icon, user_pwd, site_level
FROM user_info
WHERE user_no = #{user_no}

SELECT *
FROM user_badge NATURAL JOIN badge
WHERE user_no = #{user_no}

mapper.update:
UPDATE user_info
SET user_pwd = #{user_pwd}
WHERE user_no = #{user_no}

UPDATE user_info
SET profile_icon = #{profile_icon}
WHERE user_no = #{user_no}

mapper.delete:
DELETE FROM choice
WHERE choice_user_no = #{choice_user_no}
AND user_no = #{user_no}

mapper.update:
  		UPDATE USER_INFO 
		SET HONOR_RATE = HONOR_RATE + #{honor_rate}, honor_cnt = honor_cnt + 1
		WHERE USER_NO = #{choice_user_no}

  		update friend
		set status = #{status}
		where user_no = #{my_user_no} and friend = #{friend_summoner_id}
